#!/bin/bash\n#JS name=\"installation_test\" cores=1 mem_mb=512 stdout=\"install_test_output.log\" stderr=\"install_test_error.log\"\n\necho \"JQS 安装测试开始...\"\ndate\necho \"当前工作目录: $(pwd)\"\necho \"Python 版本: $(python3 --version)\"\necho \"可用的 Python 模块:\"\npython3 -c \"import sys; print('\\\\n'.join(sys.path))\" 2>/dev/null || echo \"Python 可能无法正常工作\"\n\necho \"睡眠 5 秒以测试作业执行...\"\nsleep 5\n\necho \"JQS 安装测试完成！\"\ndate\n